import React from "react";
import { useNavigate } from 'react-router-dom';
import Button from "../ui/Button";

function MainPage(props) {
    const navigate = useNavigate();
    
let currentSlideIndex = 0;
const slides = document.querySelectorAll(".inner li");
// 슬라이드의 총 개수를 'totalSlides' 변수에 저장합니다.
// 이는 'slides' 배열의 길이를 사용하여 계산됩니다.
const totalSlides = slides.length;

// 'showSlides'라는 함수를 정의합니다. 이 함수는 슬라이드쇼의 핵심 기능을 담당합니다.
function showSlides() {
// 'slides' 배열의 모든 요소(슬라이드)를 순회합니다.
// 'forEach' 메소드를 사용하여 배열의 각 요소에 대해 함수를 실행합니다.
slides.forEach((slide, index) => {
// 각 슬라이드에 대해, 현재 슬라이드 인덱스와 일치하는지 확인합니다.
// 일치한다면 슬라이드를 보이게 하고 (style.display = 'block'),
// 그렇지 않다면 숨깁니다 (style.display = 'none').
slide.style.display = (index === currentSlideIndex) ? "block" : "none";
});

// 현재 슬라이드 인덱스를 1 증가시킵니다.
// '%' 연산자를 사용하여 값이 'totalSlides'의 개수를 초과하지 않도록 합니다.
// 이 연산은 슬라이드가 마지막에 도달했을 때 다시 첫 번째 슬라이드로 돌아가도록 합니다.
currentSlideIndex = (currentSlideIndex + 1) % totalSlides;

// 'setTimeout' 함수를 사용하여 'showSlides' 함수를 2초(2000밀리초) 후에 다시 호출합니다.
// 이는 슬라이드가 자동으로 전환되도록 하는 타이머 역할을 합니다.
setTimeout(showSlides, 2000);
}
// 'showSlides' 함수를 처음 호출하여 슬라이드쇼를 시작합니다.
showSlides();


    return (
        <div>
            <div class="toolbar">
                <p>CLOTHZ</p>
                <a href="http://"><img src="img/profile.png" width={40} height={40} /></a>
            </div>
            <div class="outer" style={{ display: "flex", justifyContent: "center", alignItems: "center", width: "100%" , height: "50%" }}>
                <ul class="inner">
                    <li><img class="in" src="img/ike.gif" /></li>
                    <li><img class="in" src="img/ike2.gif" /></li>
                    <li><img class="in" src="img/ike3.gif" /></li>
                    <li><img class="in" src="img/ike4.gif" /></li>
                </ul>
            </div>
            <p>
                설명들어감
            </p>
            <div style={{alignContent: "space-between"}}>
                
                    <Button
                        title='스타일 추천받기'
                        onClick={() => {
                            navigate('/loading');
                        }}
                        className='recommend'
                        style={{ padding: "30px", fontSize: "30pt", alignItems: "left" }}
                    />
                
                    <Button
                        title='가상 피팅 하기'
                        onClick={() => {
                            navigate('/ListPage');
                        }}
                        className='recommend'
                        style={{ padding: "30px", fontSize: "30pt" }}
                    />
                
            </div>
        </div>
    );
}

export default MainPage;
