import React from "react";
import { useNavigate } from 'react-router-dom';
import Button from "../ui/Button";
import bg1 from "img/ike.gif";
import bg2 from "img/ike2.gif";
import bg3 from "img/ike3.gif";
import bg4 from "img/ike4.gif";

/* bg img Array */
const bgArr = [
  { img: bg1, key: 1 },
  { img: bg2, key: 2 },
  { img: bg3, key: 3 },
  { img: bg4, key: 4 },
];
const BG_NUM = bgArr.length;
const beforeSlide = bgArr[BG_NUM - 1];
const afterSlide = bgArr[0];

let slideArr = [beforeSlide, ...bgArr, afterSlide]; // create slide array (last, origin(first,...,last) ,first) for infinite slide show
const SLIDE_NUM = slideArr.length;

function MainPage(props) {
    const navigate = useNavigate();
    let currentSlideIndex = 0;
    const slides = document.querySelectorAll(".inner .in");
    const totalSlides = slides.length;

    function showSlides() {
        slides.forEach((slide, index) => {
            slide.style.display = (index === currentSlideIndex) ? "block" : "none";
        });

        currentSlideIndex = (currentSlideIndex + 1) % totalSlides;

        setTimeout(showSlides, 7000);
    }
    showSlides();

    return (
        <div>
            <div class="toolbar">
                <p>CLOTHZ</p>
                <a href="http://"><img src="img/profile.png" width={40} height={40} /></a>
            </div>
            <div class="outer" style={{ display: "flex", justifyContent: "center", alignItems: "center", width: "100%", height: "50%" }}>
                <ul class="inner">
                    <li><img class="in fade" src="img/ike.gif" /></li>
                    <li><img class="in fade" src="img/ike2.gif" /></li>
                    <li><img class="in fade" src="img/ike3.gif" /></li>
                    <li><img class="in fade" src="img/ike4.gif" /></li>
                </ul>
            </div>
            <p>
                설명들어감
            </p>
            <div style={{ alignContent: "space-between" }}>

                <Button
                    title='스타일 추천받기'
                    onClick={() => {
                        navigate('/loading');
                    }}
                    className='recommend'
                    style={{ padding: "30px", fontSize: "30pt", alignItems: "left" }}
                />

                <Button
                    title='가상 피팅 하기'
                    onClick={() => {
                        navigate('/ListPage');
                    }}
                    className='recommend'
                    style={{ padding: "30px", fontSize: "30pt" }}
                />

            </div>
        </div>
    );
}

export default MainPage;
