import React from "react";
import { useNavigate } from 'react-router-dom';
import Button from "../ui/Button";


function MainPage(props) {
    const navigate = useNavigate();
    var slideIndex = 1;
    showSlides(slideIndex);
    function showSlides(n) {
        var i;
        var slides = document.getElementsByClassName(".inner");
        var dots = document.getElementsByClassName("dot");
        if (n > slides.length) {slideIndex = 1} 
        if (n < 1) {slideIndex = slides.length}
        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none"; 
        }
        for (i = 0; i < dots.length; i++) {
            dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex-1].style.display = "block"; 
        dots[slideIndex-1].className += " active";
      }
    return (
        <div>
            <div class="toolbar">
                <p>CLOTHZ</p>
                <a href="http://"><img src="img/profile.png" width={40} height={40} /></a>
            </div>
            <div class="outer">
                <div class="inner fade"><img src="img/ike.gif" /></div>
                <div class="inner fade"><img src="img/ike2.gif" /></div>
                <div class="inner fade"><img src="img/ike3.gif" /></div>
                <div class="inner fade"><img src="img/ike4.gif" /></div>
                
            </div>
            <p>
                설명들어감
            </p>
            <div style={{ alignContent: "space-between" }}>

                <Button
                    title='스타일 추천받기'
                    onClick={() => {
                        navigate('/loading');
                    }}
                    className='recommend'
                    style={{ padding: "30px", fontSize: "30pt", alignItems: "left" }}
                />

                <Button
                    title='가상 피팅 하기'
                    onClick={() => {
                        navigate('/ListPage');
                    }}
                    className='recommend'
                    style={{ padding: "30px", fontSize: "30pt" }}
                />

            </div>
        </div>
    );
}

export default MainPage;
